import React,{Component} from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Axios from "axios";


export default class CreateStudent extends Component {
  constructor(props) {

    super(props);
    this.onChangeStudentUsuario = this.onChangeStudentUsuario.bind(this);
    this.onChangeStudentContraseña = this.onChangeStudentContraseña.bind(this);
    
    this.state={
      usuario:"",
      contraseña:"",
      
    };
  
  } 
  
  onChangeStudentUsuario(e){
    this.setState({usuario:e.target.value});
  }
  onChangeStudentContraseña(e){
    this.setState({contraseña:e.target.value});
  }
  

  onSubmit(e){
    e.preventDefault();
    const studentObject = {
      usuario:this.state.usuario,
      contraseña:this.state.contraseña,
      
    };
    Axios
      .post("http://localhost:4000/student-list", studentObject)
      .then((res)=> console.log(res.data));
    this.setState({usuario:"",contraseña:""});  
  }


  render(){

        return(
            <div classUsuario ="form-wrapper">
                <Form onSubmit={this.onSubmit}>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-thermometer-sun" viewBox="0 0 16 16">
  <path d="M5 12.5a1.5 1.5 0 1 1-2-1.415V2.5a.5.5 0 0 1 1 0v8.585A1.5 1.5 0 0 1 5 12.5z"/>
  <path d="M1 2.5a2.5 2.5 0 0 1 5 0v7.55a3.5 3.5 0 1 1-5 0V2.5zM3.5 1A1.5 1.5 0 0 0 2 2.5v7.987l-.167.15a2.5 2.5 0 1 0 3.333 0L5 10.486V2.5A1.5 1.5 0 0 0 3.5 1zm5 1a.5.5 0 0 1 .5.5v1a.5.5 0 0 1-1 0v-1a.5.5 0 0 1 .5-.5zm4.243 1.757a.5.5 0 0 1 0 .707l-.707.708a.5.5 0 1 1-.708-.708l.708-.707a.5.5 0 0 1 .707 0zM8 5.5a.5.5 0 0 1 .5-.5 3 3 0 1 1 0 6 .5.5 0 0 1 0-1 2 2 0 0 0 0-4 .5.5 0 0 1-.5-.5zM12.5 8a.5.5 0 0 1 .5-.5h1a.5.5 0 1 1 0 1h-1a.5.5 0 0 1-.5-.5zm-1.172 2.828a.5.5 0 0 1 .708 0l.707.708a.5.5 0 0 1-.707.707l-.708-.707a.5.5 0 0 1 0-.708zM8.5 12a.5.5 0 0 1 .5.5v1a.5.5 0 0 1-1 0v-1a.5.5 0 0 1 .5-.5z"/>
</svg>
                    <Form.Group controlId="Usuario">
                      <Form.Label>Usuario</Form.Label>
                      <Form.Control 
                        type="text"
                        value={this.state.usuario}
                        onChange={this.onChangeStudentUsuario}
                      />
                    </Form.Group>

                    <Form.Group controlId="Contraseña">
                      <Form.Label>Contraseña</Form.Label>
                      <Form.Control 
                        type="value"
                        value={this.state.contraseña}
                        onChange={this.onChangeStudentContraseña}
                      />
                      <br/>
                    </Form.Group>

                    <Button variant="primary" size="lg" block="block"href="http://localhost:3000/edit-student" type="submit" classUsuario="mt-4">Ingresar </Button>
                </Form>  
            </div>
        );  
    }
}
