import React,{Component} from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Axios from "axios";


export default class CreateStudent extends Component {
  constructor(props) {

    super(props);
    this.onChangeStudentUsuario = this.onChangeStudentUsuarioName.bind(this);
    this.onChangeStudentEmail = this.onChangeStudentEmail.bind(this);
    
    this.state={
      usuario:"",
      contraseña:"",
      
    };
  
  } 
  
  onChangeStudentName(e){
    this.setState({name:e.target.value});
  }
  onChangeStudentEmail(e){
    this.setState({email:e.target.value});
  }
  onChangeStudentRollno(e){
    this.setState({rollno:e.target.value});
  }

  onSubmit(e){
    e.preventDefault();
    const studentObject = {
      name:this.state.name,
      email:this.state.email,
      rollno:this.state.rollno,
    };
    Axios
      .post("http://localhost:3000/students/create-student", studentObject)
      .then((res)=> console.log(res.data));
    this.setState({name:"",email:"",rollno:""});  
  }


  render(){

        return(
            <div className ="form-wrapper">
                <h1>Ingreso de usuarios </h1>
                <Form onSubmit={this.onSubmit}>
                    <Form.Group controlId="Usuario">
                      <Form.Label>Usuario</Form.Label>
                      <Form.Control 
                        type="text"
                        value={this.state.name}
                        onChange={this.onChangeStudentName}
                      />

                    </Form.Group>

                    <Form.Group controlId="Contraseña">
                      <Form.Label>Contraseña</Form.Label>
                      <Form.Control 
                        type="value"
                        value={this.state.email}
                        onChange={this.onChangeStudentEmail}
                      />
                    </Form.Group>

                    
                    <Button variant="primary" size="lg" block="block"href="http://localhost:3000/edit-student" type="submit" className="mt-4">Ingresar </Button>
                </Form>  
            </div>
        );  
    }
}
